n.colored{color:#1b88ca}.devicon-react-original-wordmark.colored,.devicon-react-original.colored,.devicon-react-plain-wordmark.colored,.devicon-react-plain.colored{color:#61dafb}.devicon-redhat-original-wordmark.colored,.devicon-redhat-original.colored,.devicon-redhat-plain-wordmark.colored,.devicon-redhat-plain.colored{color:#e93442}.devicon-redis-plain-wordmark.colored,.devicon-redis-plain.colored{color:#d82c20}.devicon-sass-original.colored,.devicon-sass-plain-wordmark.colored,.devicon-sass-plain.colored{color:#c69}.devicon-sequelize-original-wordmark.colored,.devicon-sequelize-original.colored,.devicon-sequelize-plain-wordmark.colored,.devicon-sequelize-plain.colored{color:#3b4b72}.devicon-symfony-original-wordmark.colored,.devicon-symfony-original.colored,.devicon-symfony-plain-wordmark.colored,.devicon-symfony-plain.colored{color:#1a171b}.devicon-travis-plain-wordmark.colored,.devicon-travis-plain.colored{color:#bb2031}.devicon-trello-plain-wordmark.colored,.devicon-trello-plain.colored{color:#23719f}.devicon-twitter-original.colored,.devicon-twitter-plain.colored{color:#1da1f2}.devicon-typescript-original.colored,.devicon-typescript-plain.colored{color:#007acc}.devicon-ubuntu-plain-wordmark.colored,.devicon-ubuntu-plain.colored{color:#dd4814}.devicon-vim-plain.colored{color:#179a33}.devicon-windows8-original-wordmark.colored,.devicon-windows8-original.colored,.devicon-windows8-plain-wordmark.colored,.devicon-windows8-plain.colored{color:#00adef}.devicon-wordpress-plain-wordmark.colored,.devicon-wordpress-plain.colored{color:#494949}.devicon-yii-plain-wordmark.colored,.devicon-yii-plain.colored{color:#0073bb}.devicon-zend-plain-wordmark.colored,.devicon-zend-plain.colored{color:#68b604}*,[class*=" devicon-"],[class^=devicon-]{-webkit-font-smoothing:antialiased;-moz-osx-font-smoothing:grayscale}body{margin:0;padding:0;font-size:100%;line-height:1.5;color:#555;font-family:'Antic Slab',serif}article,aside,figcaption,figure,footer,header,nav,section{display:block}